    accent: "#FFEB00",
    background: "#202020",
    detailBackground: "#fff",
    surface: "#202020",
    error: '#B00020',
    text: "#fff",
    detailText: "#202020",
    onSurface: '#000000',
    disabled: "rgb(255, 69, 58)",
